import * as utils from './utils.js'

export async function init(apiKeyX, urls) {
    if (typeof apiKeyX !== "string" || typeof urls !== "string") {
        console.error('Send details in correct format');
        return;
    }

    const [isValidKey, isValidUrl] = await Promise.all([
        utils.apiKeyValidator(apiKeyX),
        utils.urlValidator(urls)
    ]);

    if (!isValidKey && !isValidUrl)
        return;

    utils.addContloScript(apiKeyX);
}
